mapper.update:
UPDATE trip_join_request
SET status = #{status}
WHERE trip_article_id = #{tripArticleId}
AND host_user_id = #{hostUserId}
AND applicant_user_id = #{applicantUserId}

UPDATE trip_join_request
SET status = #{status}
WHERE trip_article_id = #{tripArticleId}
AND host_user_id = #{hostUserId}
AND applicant_user_id = #{applicantUserId}

mapper.select:
SELECT
    EXISTS(
        SELECT 1
        FROM trip_join_request
        WHERE trip_article_id = #{travelArticleId}
        AND applicant_user_id = #{applicantUserId}
        AND status IN ('PENDING')
    ) AS has_pending,
    EXISTS(
        SELECT 1
        FROM chat_participants cp
        JOIN chat_room cr
        ON cp.chat_room_id = cr.id
        WHERE cr.trip_article_id = #{travelArticleId}
          AND cp.user_id = #{applicantUserId}
          AND cp.status = 'ACTIVE'
    ) AS has_active
